script:
frappe.ui.form.on('Item', {
    onload(frm) {
        if (frm.is_new()) {
            frm.set_value("item_name", "");
        }
    },
    custom_item_name_combination(frm) {
        if (frm.doc.custom_item_name_combination == 1) {
            open_item_combination_dialog(frm);
        }
    },
    custom_spice: update_fields,
    custom_qty: update_fields,
    custom_uom: update_fields,
    custom_group_abbr: update_fields,
    custom_packing_type: update_fields,
    custom_packing_type_abbr: update_fields
});

function open_item_combination_dialog(frm) {
    let dialog = new frappe.ui.Dialog({
        title: 'Item Name Combination Setup',
        size: 'large',
        fields: [
            {
                fieldname: 'spices',
                fieldtype: 'Link',
                label: 'Select Spices',
                options: 'Spices',
                reqd: 1,
                change: function() {
                    let selected_spice = dialog.get_value('spices');
                    if (selected_spice) {
                        frm.set_value('custom_spice', selected_spice);
                        update_fields(frm);
                    }
                }
            },
            { fieldtype: "Column Break" },
            {
                fieldname: 'qty',
                fieldtype: 'Link',
                label: 'Select Quantity',
                options: 'Qty',
                reqd: 1,
                change: function() {
                    let selected_qty = dialog.get_value('qty');
                    if (selected_qty) {
                        frm.set_value('custom_qty', selected_qty);
                        update_fields(frm);
                    }
                }
            },{ fieldtype: "Section Break" },
            {
                fieldname: 'uom',
                fieldtype: 'Link',
                label: 'Select UOM',
                options: 'UOM',
                reqd: 1,
                change: function() {
                    let selected_uom = dialog.get_value('uom');
                    if (selected_uom) {
                        frm.set_value('custom_uom', selected_uom);
                        update_fields(frm);
                    }
                }
            },
            { fieldtype: "Column Break" },
            {
                fieldname: 'packing_type',
                fieldtype: 'Link',
                label: 'Select Packing Type',
                options: 'Packing Type',
                reqd: 1,
                change: function() {
                    let selected_packing = dialog.get_value('packing_type');
                    if (selected_packing) {
                        frm.set_value('custom_packing_type', selected_packing);
                        update_fields(frm);
                    }
                }
            }
        ],
        primary_action_label: 'Apply Selection',
        primary_action(values) {
            // Set all selected values to form
            if (values.spices) frm.set_value('custom_spice', values.spices);
            if (values.qty) frm.set_value('custom_qty', values.qty);
            if (values.uom) frm.set_value('custom_uom', values.uom);
            if (values.packing_type) frm.set_value('custom_packing_type', values.packing_type);
            
            // Trigger update function
            update_fields(frm);
            
            dialog.hide();
            // frappe.msgprint('Item name combination updated successfully!');
        },
        secondary_action_label: 'Cancel',
        secondary_action() {
            dialog.hide();
        }
    });
    
    // Pre-populate existing values if any
    if (frm.doc.custom_spice) dialog.set_value('spices', frm.doc.custom_spice);
    if (frm.doc.custom_qty) dialog.set_value('qty', frm.doc.custom_qty);
    if (frm.doc.custom_uom) dialog.set_value('uom', frm.doc.custom_uom);
    if (frm.doc.custom_packing_type) dialog.set_value('packing_type', frm.doc.custom_packing_type);
    
    dialog.show();
}

function update_fields(frm) {
    if (frm.doc.custom_item_name_combination != 1) {
        return;
    }
    const groupAbbr = (frm.doc.custom_group_abbr || "").trim();
    const itemGroup = (frm.doc.item_group || "").trim().toLowerCase();
    let spice = (frm.doc.custom_spice || "").trim();
    const qty = (frm.doc.custom_qty || "").toString().trim();
    const uom = (frm.doc.custom_uom || "").trim();
    const packingType = (frm.doc.custom_packing_type || "").trim();
    let packingAbbr = (frm.doc.custom_packing_type_abbr || "").trim();
    
    // Capitalize packing abbreviation
    packingAbbr = capitalizeFirstLetter(packingAbbr);
    
    // Remove duplicate item group word from end of spice
    if (spice && spice.toLowerCase().endsWith(itemGroup)) {
        spice = spice.slice(0, -itemGroup.length).trim();
    }
    
    // If nothing is provided, reset fields
    if (!(groupAbbr || spice || qty || uom || packingAbbr)) {
        frm.set_value("item_name", "");
        frm.set_value("description", "");
        return;
    }
    
    // --- Build Item Name ---
    const itemNameParts = [];
    if (groupAbbr) itemNameParts.push(groupAbbr);
    if (spice) itemNameParts.push(spice);
    if (qty || uom) itemNameParts.push(`${qty}${uom}`.trim());
    if (packingAbbr) itemNameParts.push(packingAbbr);
    const itemName = itemNameParts.filter(Boolean).join(" - ");
    
    // --- Build Description ---
    const descParts = [];
    if (spice) descParts.push(spice);
    if (qty || uom) descParts.push(`${qty}${uom}`.trim());
    if (packingType) descParts.push(packingType);
    const description = descParts.filter(Boolean).join(" ").replace(/\s+/g, " ").trim();
    
    frm.set_value("item_name", itemName);
    frm.set_value("description", description);
}

function capitalizeFirstLetter(str) {
    if (!str) return "";
    return str.charAt(0).toUpperCase() + str.slice(1);
}

frappe.ui.form.on('Item', {
    onload: function(frm) {
        console.log("Form loaded - Debug Info:", {
            is_new: frm.is_new(),
            custom_group_abbr: frm.doc.custom_group_abbr,
            item_group: frm.doc.item_group
        });
        
        // Check conditions for specific item groups only
        if (frm.is_new() && frm.doc.custom_group_abbr && frm.doc.item_group) {
            if (frm.doc.item_group === "Selling Items" || frm.doc.item_group === "Buying Items") {
                generate_item_code(frm);
            }
        }
    },
    
    item_group: function(frm) {
        console.log("Item group changed to:", frm.doc.item_group);
        
        setTimeout(() => {
            if (frm.doc.custom_group_abbr && frm.doc.item_group) {
                if (frm.doc.item_group === "Selling Items" || frm.doc.item_group === "Buying Items") {
                    generate_item_code(frm);
                } else {
                    frm.set_value('item_code', '');
                    console.log("Item group not allowed, cleared item code");
                }
            }
        }, 100);
    },
    
    item_name: function(frm) {
        if (frm.doc.custom_group_abbr && 
            (frm.doc.item_group === "Selling Items" || frm.doc.item_group === "Buying Items")) {
            generate_item_code(frm);
        }
    },
    
    custom_group_abbr: function(frm) {
        console.log("Custom group abbr changed to:", frm.doc.custom_group_abbr);
        
        setTimeout(() => {
            if (frm.doc.custom_group_abbr && frm.doc.item_group) {
                if (frm.doc.item_group === "Selling Items" || frm.doc.item_group === "Buying Items") {
                    generate_item_code(frm);
                } else {
                    frm.set_value('item_code', '');
                    console.log("Item group not allowed, cleared item code");
                }
            } else if (!frm.doc.custom_group_abbr) {
                frm.set_value('item_code', '');
                console.log("Custom group abbr cleared, cleared item code");
            }
        }, 100);
    }
});

function generate_item_code(frm) {
    let abbr = frm.doc.custom_group_abbr;
    
    if (!abbr || !frm.doc.item_group) {
        console.log("Conditions not met:", {
            abbr: abbr,
            item_group: frm.doc.item_group
        });
        return;
    }
    
    if (frm.doc.item_group !== "Selling Items" && frm.doc.item_group !== "Buying Items") {
        console.log("Item group not allowed for auto-generation:", frm.doc.item_group);
        return;
    }
    
    console.log("Generating item code for:", {
        abbr: abbr,
        item_group: frm.doc.item_group
    });
    
    frappe.call({
        method: "frappe.client.get_list",
        args: {
            doctype: "Item",
            filters: [
                ["item_code", "like", abbr + "-%"]
            ],
            fields: ["item_code"],
            order_by: "item_code desc",
            limit_page_length: 1
        },
        callback: function(r) {
            console.log("API Response:", r);
            
            let new_sequence = "001";  
            
            if (r.message && r.message.length > 0) {
                let last_item_code = r.message[0].item_code;
                console.log("Last item code:", last_item_code);
                
                let parts = last_item_code.split('-');
                if (parts.length >= 2) {
                    let last_sequence = parts[parts.length - 1]; 
                    let next_sequence = parseInt(last_sequence) + 1;
                    
                    if (isNaN(next_sequence)) {
                        next_sequence = 1;
                    }
                    
                    new_sequence = String(next_sequence).padStart(3, '0');
                }
            }
            
            let new_item_code = `${abbr}-${new_sequence}`;
            console.log("Generated item code:", new_item_code);
            
            frm.set_value('item_code', new_item_code).then(() => {
                if (frm.doc.item_name) {
                    frm.set_value('item_name', "");
                }
                frm.refresh_field('item_code');
            }).catch(err => {
                console.error("Error setting item code:", err);
            });
        },
        error: function(err) {
            console.error("API Error:", err);
        }
    });
}

frappe.ui.form.on('Role Permission for Page and Report', {
  page:function(frm) {
    if(frm.doc.roles.length < 1){
            frm.clear_custom_buttons();
        frm.page.clear_actions();
    }
  },
  update_report_page_data: function (frm) {
        if (frm.doc.page == "backups"){
            frm.clear_custom_buttons();
        frm.page.clear_actions();
        frm.set_query('page', function() {
            return {
                filters: [
                    ['name', '!=', 'backups']
                    ['name', '!=', 'permission-manager']
                ]
            };
        });
    }}
});

frappe.ui.form.on('Customer', {
    // Trigger on form load
    onload: function(frm) {
        set_customer_group_if_individual(frm);
        set_tax_category_based_on_territory(frm);
        set_default_accounts_and_credit_limits(frm);
    },
    
    // Trigger when customer_type field changes
    customer_type: function(frm) {
        set_customer_group_if_individual(frm);
        set_tax_category_based_on_territory(frm);
        set_default_accounts_and_credit_limits(frm);
    },
    
    // Trigger when territory field changes
    territory: function(frm) {
        set_tax_category_based_on_territory(frm);
    },
    
    // Mobile number validation
    custom_mobile: function(frm) {
        validate_mobile_number(frm);
    },
    
    // Validate before save
    validate: function(frm) {
        validate_mobile_number(frm);
    }
});

function set_customer_group_if_individual(frm) {
    if (frm.doc.customer_type === 'Individual') {
        frm.set_value('customer_group', 'Individual');
    }
}

function set_tax_category_based_on_territory(frm) {
    if (frm.doc.customer_type === 'Individual' && frm.doc.territory) {
        // Check if territory is under Kerala
        frappe.call({
            method: 'frappe.client.get',
            args: {
                doctype: 'Territory',
                name: frm.doc.territory
            },
            callback: function(r) {
                if (r.message) {
                    check_territory_hierarchy(frm, r.message);
                }
            }
        });
    }
}

function check_territory_hierarchy(frm, territory_doc) {
    // If current territory is Kerala, set In-State
    if (territory_doc.name === 'Kerala') {
        frm.set_value('tax_category', 'In-State');
        return;
    }
    
    // If territory has a parent, check the parent recursively
    if (territory_doc.parent_territory && territory_doc.parent_territory !== 'All Territories') {
        frappe.call({
            method: 'frappe.client.get',
            args: {
                doctype: 'Territory',
                name: territory_doc.parent_territory
            },
            callback: function(r) {
                if (r.message) {
                    if (r.message.name === 'Kerala') {
                        frm.set_value('tax_category', 'In-State');
                    } else if (r.message.parent_territory && r.message.parent_territory !== 'All Territories') {
                        // Continue checking up the hierarchy
                        check_territory_hierarchy(frm, r.message);
                    } else {
                        // Reached top level without finding Kerala
                        frm.set_value('tax_category', 'Out-State');
                    }
                } else {
                    frm.set_value('tax_category', 'Out-State');
                }
            }
        });
    } else {
        // No parent or reached top level, set Out-State
        frm.set_value('tax_category', 'Out-State');
    }
}

function set_default_accounts_and_credit_limits(frm) {
    if (frm.doc.customer_type === 'Individual') {
        // Auto-fill accounts child table
        set_default_accounts(frm);
        
        // Auto-fill credit_limits child table
        set_default_credit_limits(frm);
    }
}

function set_default_accounts(frm) {
    // Check if accounts table is empty or doesn't have the required entry
    let company = "Auraspace Traders Pvt Ltd";
    let account = "Debtors INR - AT";
    
    let existing_row = frm.doc.accounts.find(row => 
        row.company === company && row.account === account
    );
    
    if (!existing_row) {
        // Check if there's an empty row or create new one
        let empty_row = frm.doc.accounts.find(row => !row.company && !row.account);
        
        if (empty_row) {
            // Fill the empty row
            frappe.model.set_value(empty_row.doctype, empty_row.name, 'company', company);
            frappe.model.set_value(empty_row.doctype, empty_row.name, 'account', account);
        } else {
            // Add new row
            let new_row = frm.add_child('accounts');
            new_row.company = company;
            new_row.account = account;
        }
        
        frm.refresh_field('accounts');
    }
}

function set_default_credit_limits(frm) {
    // Check if credit_limits table is empty or doesn't have the required entry
    let company = "Auraspace Traders Pvt Ltd";
    
    let existing_row = frm.doc.credit_limits.find(row => row.company === company);
    
    if (!existing_row) {
        // Check if there's an empty row or create new one
        let empty_row = frm.doc.credit_limits.find(row => !row.company);
        
        if (empty_row) {
            // Fill the empty row
            frappe.model.set_value(empty_row.doctype, empty_row.name, 'company', company);
        } else {
            // Add new row
            let new_row = frm.add_child('credit_limits');
            new_row.company = company;
        }
        
        frm.refresh_field('credit_limits');
    }
}

// Mobile number validation function
function validate_mobile_number(frm) {
    if (frm.doc.customer_type === 'Individual' && frm.doc.custom_mobile) {
        let mobile = String(frm.doc.custom_mobile);
        
        // Remove any spaces, hyphens, or other non-digit characters
        mobile = mobile.replace(/\D/g, '');
        
        // Check if it's exactly 10 digits
        if (mobile.length !== 10) {
            frappe.msgprint({
                title: __('Invalid Mobile Number'),
                indicator: 'red',
                message: __('Mobile number must be exactly 10 digits for Individual customers.')
            });
            frm.set_focus('custom_mobile');
            // Clear invalid value or keep it for user to correct
            // frm.set_value('custom_mobile', '');
            return false;
        }
        
        // Check if it starts with valid Indian mobile prefixes (optional)
        let validPrefixes = ['6', '7', '8', '9'];
        if (!validPrefixes.includes(mobile[0])) {
            frappe.msgprint({
                title: __('Invalid Mobile Number'),
                indicator: 'orange',
                message: __('Mobile number should start with 6, 7, 8, or 9 for Indian numbers.')
            });
            frm.set_focus('custom_mobile');
            return false;
        }
        
        // If validation passes, update the field with clean number
        if (String(frm.doc.custom_mobile) !== mobile) {
            frm.set_value('custom_mobile', parseInt(mobile));
        }
        
        return true;
    }
    return true;
}

// Link formatter for Item field
frappe.form.link_formatters['Item'] = function(value, doc) {
    if (doc.item_name && doc.item_name !== value) {
        return value + ': ' + doc.item_name;
    } else {
        return value;
    }
};

frappe.ui.form.on("Sales Invoice", {
    refresh: function(frm) {
        let zero_qty_items = frm.doc.items.filter(i => i.actual_qty == 0);

        if (zero_qty_items.length > 0) {
            frm.add_custom_button(__('Create Product Bundle'), function() {
                open_product_bundle_dialog(frm, zero_qty_items);
            });
        }
    }
});

function open_product_bundle_dialog(frm, zero_qty_items) {
    let parent_item_options = zero_qty_items.map(i => i.item_code);

    let dialog = new frappe.ui.Dialog({
        title: "Create Product Bundle",
        size: "large",
        fields: [
            {
                label: "Parent Item",
                fieldname: "parent_item",
                fieldtype: "Select",
                options: parent_item_options,
                reqd: 1,
                change: function() {
                    let selected_item = zero_qty_items.find(i => i.item_code === dialog.get_value("parent_item"));
                    if (selected_item) {
                        dialog.set_value("parent_description", selected_item.description);
                    }
                }
            },
            {
                label: "Description",
                fieldname: "parent_description",
                fieldtype: "Small Text",
                read_only: 0
            },
            {
                fieldtype: "Section Break"
            },
            {
                fieldname: "bundle_items",
                fieldtype: "Table",
                label: "Bundle Items",
                cannot_add_rows: false,
                in_place_edit: false,
                fields: [
                    {
                        fieldname: "item_code",
                        label: "Item Code", 
                        fieldtype: "Link",
                        options: "Item",
                        in_list_view: 1,
                        reqd: 1,
                        columns: 4,
                        get_query: function() {
                            let valid_item_groups = ["Buying Items", "Selling Items"];
                            
                            frappe.call({
                                method: "frappe.client.get_list",
                                args: {
                                    doctype: "Item Group",
                                    fields: ["name"],
                                    filters: [
                                        ["parent_item_group", "in", ["Buying Items", "Selling Items"]]
                                    ]
                                },
                                async: false,
                                callback: function(r) {
                                    if (r.message) {
                                        valid_item_groups = valid_item_groups.concat(r.message.map(d => d.name));
                                    }
                                }
                            });
                            
                            return {
                                filters: {
                                    item_group: ["in", valid_item_groups],
                                    disabled: 0
                                }
                            };
                        }
                    },
                    {
                        fieldname: "qty",
                        label: "Qty",
                        fieldtype: "Float", 
                        in_list_view: 1,
                        reqd: 1,
                        columns: 1.5,
                        precision: 2
                    },
                    {
                        fieldname: "uom",
                        label: "UOM",
                        fieldtype: "Link",
                        options: "UOM",
                        in_list_view: 1,
                        columns: 1.5
                    },
                    {
                        fieldname: "description",
                        label: "Description",
                        fieldtype: "Small Text",
                        in_list_view: 0,
                        columns: 5
                    }
                ]
            }
        ],
        primary_action_label: "Create Product Bundle",
        primary_action(values) {
            if (!values.parent_item) {
                frappe.msgprint("Parent Item is required");
                return;
            }

            frappe.call({
                method: "frappe.client.insert",
                args: {
                    doc: {
                        doctype: "Product Bundle",
                        new_item_code: values.parent_item,
                        description: values.parent_description,
                        items: values.bundle_items || []
                    }
                },
                callback: function(r) {
                    if (!r.exc) {
                        frappe.msgprint("Product Bundle Created: " + r.message.name);
                        dialog.hide();
                        frm.reload_doc();
                    }
                }
            });
        }
    });

    dialog.set_secondary_action(function() {
        window.open(`/app/item/new-item-1`, "_blank");
    });
    dialog.set_secondary_action_label("Create New Item");

    if (parent_item_options.length > 0) {
        dialog.set_value("parent_item", parent_item_options[0]);
        dialog.set_value("parent_description", zero_qty_items[0].description);
    }

    dialog.show();

    setTimeout(() => {
        setupItemEvents(dialog);
    }, 500);
}

function setupItemEvents(dialog) {
    let wrapper = dialog.$wrapper;
    let table = wrapper.find('[data-fieldname="bundle_items"]');
    
    table.off('change.item_uom');
    table.on('change.item_uom', '[data-fieldname="item_code"] input', function(e) {
        let $input = $(this);
        let item_code = $input.val();
        
        if (item_code) {
            let $row = $input.closest('.grid-row');
            let row_index = $row.attr('data-idx') - 1;
            let grid_data = dialog.fields_dict.bundle_items.grid.data;
            let row_doc = grid_data[row_index];
            
            if (row_doc) {
                // Fetch item details
                frappe.call({
                    method: "frappe.client.get_value",
                    args: {
                        doctype: "Item",
                        fieldname: ["stock_uom", "item_name", "description"],
                        filters: {name: item_code}
                    },
                    callback: function(r) {
                        if (r.message) {
                            row_doc.uom = r.message.stock_uom || "";
                            row_doc.description = r.message.description || "";
                            
                            let $uom_input = $row.find('[data-fieldname="uom"] input');
                            let $desc_input = $row.find('[data-fieldname="description"] textarea');
                            
                            $uom_input.val(r.message.stock_uom || "");
                            $desc_input.val(r.message.description || "");
                            
                            $uom_input.trigger('change');
                            $desc_input.trigger('change');
                        }
                    }
                });
            }
        }
    });

    table.on('blur.item_uom', '[data-fieldname="item_code"] .link-field input', function(e) {
        $(this).trigger('change.item_uom');
    });
}
